INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239038, 'Carbamazepine', 'Cabotegravir', 'Major', 'Coadministration with certain anticonvulsants or antimycobacterials may decrease the plasma concentrations of cabotegravir.  The mechanism has not been clearly established but may involve induction of uridine diphosphate glucuronosyltransferase (UGT)-mediated metabolism of cabotegravir, which is primarily metabolized by UGT1A1 and to a lesser extent by UGT1A9.', NULL, 'Per the manufacturer, concomitant use of cabotegravir with carbamazepine, oxcarbazepine, phenobarbital, phenytoin, rifampin, or rifapentine is considered contraindicated.', NULL, 'Per the manufacturer, concomitant use of cabotegravir with carbamazepine, oxcarbazepine, phenobarbital, phenytoin, rifampin, or rifapentine is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/251480/', '[1] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[2] "Product Information. Cabenuva (cabotegravir-rilpivirine)." ViiV Healthcare ULC  (2021):[3] "Product Information. Vocabria (cabotegravir)." ViiV Healthcare  (2021):', 1767363027);
