INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239329, 'Pralsetinib', 'Artesunate', 'Moderate', 'Coadministration with pralsetinib may increase the plasma concentrations of drugs that are P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic anion transporting polypeptide 1B1 (OATP1B1), OATP1B3, organic anion transporter 1 (OAT1), multidrug and toxin extrusion 1 (MATE1), and/or MATE2-K substrates.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OATP1B1, OATP1B3, OAT1, MATE1, and/or MATE2-K, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with substrates of these transporters for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/253442/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicin