INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239454, 'Hydroxyzine', 'Botulinum toxin type B', 'Moderate', 'Use of anticholinergic drugs after administration of botulinum toxin may potentiate systemic anticholinergic effects such as dry mouth, blurred vision, and urinary disorders.  Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive or synergistic anticholinergic effects may occur when these agents are used together.', NULL, 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.', NULL, 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) are used after administration of botulinum toxin.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/254685/', '[1] "Product Information. Myobloc (rimabotulinumtoxinB)." Elan Pharmaceuticals[2] "Product Information. Dysport (abobotulinumtoxinA)." Tercica Inc[3] "Product Information. Botox (onabotulinumtoxinA)." Allergan Inc[4] "Product Information. Xeomin (incobotulinumtoxinA)." Merz Pharmaceuticals  (2022):[5] "Product Information. Jeuveau (prabotulinumtoxinA)." Evolus, Inc.  (2022):[6] "Product Information. Daxxify (daxibotulinumtoxinA)." Revance Therapeutics, Inc.  (2022):', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239455, 'Bromocriptine', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of bromocriptine.  Orally administered bromocriptine is extensively metabolized in the gastrointestinal tract and liver by CYP450 3A4, with approximately 93% of the absorbed dose undergoing first-pass metabolism and only the remaining 7% reaching systemic circulation.', NULL, 'Concomitant use of bromocriptine with potent CYP450 3A4 inhibitors should be avoided when possible.', NULL, 'Concomitant use of bromocriptine with potent CYP450 3A4 inhibitors should be avoided when possible.  In patients who have been treated with a potent CYP450 3A4 inhibitor, an adequate washout of the inhibitor (approximately 3 to 5 elimination half-lives) is recommended prior to initiation of bromocriptine.  If coadministration with a potent CYP450 3A4 inhibitor is required, bromocriptine dosage should be reduced to avoid toxicity.  Caution and close monitoring for development of adverse effects are advisable, and the bromocriptine dosage further adjusted if necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/254700/', '[1] Nelson MV, Berchou RC, Kareti D, Le Witt PA "Pharmacokinetic evaluation of erythromycin and caffeine administered with bromocriptine." Clin Pharmacol Ther 47 (1990):  694-7[2] "Product Information. Parlodel (bromocriptine)." Sandoz Pharmaceuticals Corporation  (2001):[3] von Rosenstiel NA, Adam D "Macrolide antibacterials. Drug interactions of clinical significance." Drug Saf 13 (1995):  105-22[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Periti P, Mazzei T, Mini E, Novelli A "Pharmacokinetic drug interactions of macrolides." Clin Pharmacokinet 23 (1992):  106-31[7] "Product Information. Cycloset (bromocriptine)." Valeant Pharmaceuticals  (2018):', 1767363027);
