INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239510, 'Daclatasvir', 'Rosiglitazone', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/255843/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sovaldi (sofosbuvir)." Gilead Sciences  (2013):[4] "Product Information. Harvoni (ledipasvir-sofosbuvir)." Gilead Sciences  (2014):[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[6] "Product Information. Daklinza (daclatasvir)." Bristol-Myers Squibb  (2015):[7] Cerner Multum, Inc "Malaysia product information." O 0 (2015):[8] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Co., Inc  (2016):[9] "Product Information. Epclusa (sofosbuvir-velpatasvir)." Gilead Sciences  (2016):[10] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences  (2017):[11] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239511, 'Fluocinolone acetonide', 'Rosiglitazone', 'Minor', 'The efficacy of insulin and other antidiabetic agents may be diminished by topical corticosteroids, particularly during prolonged or indiscriminate use.  Corticosteroids can raise blood glucose level by antagonizing the action and suppressing the secretion of insulin, which results in inhibition of peripheral glucose uptake and increased gluconeogenesis.', NULL, 'Systemic absorption of topical corticosteroids may occur depending on the vehicle and concentration of the preparation, the size of the application area, the integrity of the skin, and the duration of administration.', NULL, 'Systemic absorption of topical corticosteroids may occur depending on the vehicle and concentration of the preparation, the size of the application area, the integrity of the skin, and the duration of administration.  Use of occlusive dressings over the applied areas may also increase percutaneous absorption.  Given equivalent doses, small children are generally at the greatest risk because of their larger skin surface to body mass ratios.  If possible, the use of highly potent agents (e.g., augmented betamethasone, clobetasol, diflorasone, and halobetasol) should be avoided in children and limited to small areas for 2 weeks in adults.  Close clinical monitoring of glycemic control is recommended if topical corticosteroids are administered chronically and/or to large areas in diabetic patients.', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/255845/', '[1] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986):  139-42[2] Ludvik B, Clodi M, Kautzky-Willer A, Capek M, Hartter E, Pacini G, Prager R "Effect of dexamethasone on insulin sensitivity, islet amyloid polypeptide and insulin secretion in humans." Diabetologia 36 (1993):  84-7[3] Carruthers JA, Staughton RC, August PJ "Penetration of topical steroid preparations." Arch Dermatol 113 (1977):  522[4] Pace WE "Topical corticosteroids." Can Med Assoc J 108 (1973):  11 passim[5] "Product Information. Ultravate (halobetasol topical)." Apothecon Inc  (2022):[6] "Product Information. Diprolene (betamethasone topical)." Schering Corporation  (2001):[7] "Product Information. Temovate (clobetasol topical)." Glaxo Wellcome[8] "Product Information. Psorcon (diflorasone topical)." Rhone Poulenc Rorer  (2001):', 1767365210);
