INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239581, 'Selumetinib', 'Baricitinib', 'Moderate', 'Coadministration with selumetinib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of organic anion transporter 3 (OAT3).  The proposed mechanism, based on in vivo data, is decreased clearance due to selumetinib-mediated inhibition of OAT3 transporter.', NULL, 'Caution is advised if selumetinib is used concomitantly with drugs that are substrates of OAT3, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if selumetinib is used concomitantly with drugs that are substrates of OAT3, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring of the OAT3 substrate should be considered whenever selumetinib is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256438/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation  (2012):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239582, 'Yellow Fever Vaccine', 'Baricitinib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256453/', '[1] "Product Information. Meruvax II (rubella virus vaccine)." Merck & Co., Inc  (2022):[2] "Product Information. Attenuvax (measles virus vaccine)." Merck & Co., Inc  (2022):[3] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur  (2001):[4] Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):[5] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993):  1-18[6] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Co., Inc  (2002):[7] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011):  1495-7[8] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014):  161[9] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence.  https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf"   (2019):[10] "Product Information. DENGVAXIA (dengue vaccine)." sanofi pasteur  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239583, 'Varicella Zoster Vaccine (Recombinant)', 'Baricitinib', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition.  Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents (e.g., greater than or equal to 2 mg/kg/day or 20 mg/day of prednisone or equivalent for 14 consecutive days or more), or long-term topical or inhaled corticosteroids.', NULL, 'In general, the U.', NULL, 'In general, the U.S. Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.  However, higher dosages, more frequent boosters, and/or serological testing may be required in some cases.  Local guidelines and prescribing information for individual vaccines should be consulted.  For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease.  For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.  For SARS-CoV-2 (COVID-19) vaccines, vaccination should preferably be completed at least two weeks before initiation or resumption of immunosuppressive therapies; however, decisions to delay or temporarily withhold immunosuppressive therapy to complete COVID-19 vaccination should consider the individual''s risks relative to their underlying condition.  Additional shots and/or boosters may be appropriate depending on age, prior COVID-19 vaccine formulation(s) received, and other factors in individuals with moderate to severe immune compromise due to medical conditions or immunosuppressive medications or treatments (e.g., solid organ transplant recipients on immunosuppressive therapy; patients on active treatment for solid tumor and hematologic malignancies).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256454/', '[1] "Product Information. Fluzone (influenza virus vaccine, inactivated)." Connaught Laboratories Inc[2] "Product Information. Omnihib (haemophilus b conjugate vaccine (obsolete))." SmithKline Beecham[3] "Product Information. Havrix (hepatitis A adult vaccine)." SmithKline Beecham[4] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993):  1-18[5] "Product Information. Imovax Rabies (rabies vaccine, human diploid cell)." sanofi pasteur  (2022):[6] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc.  (2003):[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline  (2022):[9] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence.  https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf"   (2019):[10] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations.  https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chapter_6.pdf"   (2019):[11] CDC Centers for Disease Control and Prevention "Interim Clinical Considerations for Use of COVID-19 Vaccines Currently Approved or Authorized in the United States.  https://www.cdc.gov/vaccines/covid-19/clinical-considerations/interim-considerations-us.html"   (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239584, 'Insulin glargine', 'Daclatasvir', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256458/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sovaldi (sofosbuvir)." Gilead Sciences  (2013):[4] "Product Information. Harvoni (ledipasvir-sofosbuvir)." Gilead Sciences  (2014):[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[6] "Product Information. Daklinza (daclatasvir)." Bristol-Myers Squibb  (2015):[7] Cerner Multum, Inc "Malaysia product information." O 0 (2015):[8] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Co., Inc  (2016):[9] "Product Information. Epclusa (sofosbuvir-velpatasvir)." Gilead Sciences  (2016):[10] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences  (2017):[11] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239585, 'Insulin glargine', 'Fluocinolone acetonide', 'Minor', 'The efficacy of insulin and other antidiabetic agents may be diminished by topical corticosteroids, particularly during prolonged or indiscriminate use.  Corticosteroids can raise blood glucose level by antagonizing the action and suppressing the secretion of insulin, which results in inhibition of peripheral glucose uptake and increased gluconeogenesis.', NULL, 'Systemic absorption of topical corticosteroids may occur depending on the vehicle and concentration of the preparation, the size of the application area, the integrity of the skin, and the duration of administration.', NULL, 'Systemic absorption of topical corticosteroids may occur depending on the vehicle and concentration of the preparation, the size of the application area, the integrity of the skin, and the duration of administration.  Use of occlusive dressings over the applied areas may also increase percutaneous absorption.  Given equivalent doses, small children are generally at the greatest risk because of their larger skin surface to body mass ratios.  If possible, the use of highly potent agents (e.g., augmented betamethasone, clobetasol, diflorasone, and halobetasol) should be avoided in children and limited to small areas for 2 weeks in adults.  Close clinical monitoring of glycemic control is recommended if topical corticosteroids are administered chronically and/or to large areas in diabetic patients.', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256460/', '[1] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986):  139