INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239655, 'Berotralstat', 'Ripretinib', 'Moderate', 'Coadministration of moderate or weak CYP450 3A4 inhibitors with ripretinib may increase the plasma concentrations and the risk of adverse effects (e.g., palmar-plantar erythrodysesthesia, hypertension, cardiac dysfunction, arthralgia/myalgia) of ripretinib and its active metabolite (DP-5439).  The proposed mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', NULL, 'Caution is recommended when ripretinib is used concomitantly with a moderate or weak CYP450 3A4 inhibitor.', NULL, 'Caution is recommended when ripretinib is used concomitantly with a moderate or weak CYP450 3A4 inhibitor.  Clinical and laboratory monitoring should be considered whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with ripretinib, and the dosage adjusted as necessary.  Patients should be monitored for development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256849/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Qinlock (ripretinib)." Deciphera Pharmaceuticals  (2020):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239656, 'Berotralstat', 'Romidepsin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of romidepsin, which is a substrate of the efflux transporter.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors.  Pharmacologic response to romidepsin should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the romidepsin dosage adjusted as necessary.  Patients should have complete blood cell counts, electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256850/', '[1] "Product Information. Istodax (romidepsin)." Gloucester Pharmaceuticals  (2009):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239657, 'Berotralstat', 'Selumetinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5.  Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, a reduction in the dosage of selumetinib is recommended.  Patients receiving selumetinib 25 mg/m2 twice daily should have the dosage reduced to 20 mg/m2 twice daily, and those receiving 20 mg/m2 twice daily should have the dosage reduced to 15 mg/m2 twice daily.  Further dosage adjustments should be made according to clinical response and tolerance.  Please refer to the product labeling for more detailed information on dosing adjustments.  After discontinuation of the strong or moderate CYP450 3A4 inhibitor for 3 elimination half-lives, the selumetinib dosage that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256851/', '[1] "Product Information. Koselugo (selumetinib)." Astra-Zeneca Pharmaceuticals  (2020):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239658, 'Berotralstat', 'Tivozanib', 'Moderate', 'Coadministration with tivozanib may increase the plasma concentrations and therapeutic effects of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter.  The proposed mechanism, based on in vitro data, is decreased clearance due to inhibition of BCRP-mediated intestinal efflux by tivozanib.', NULL, 'Caution and clinical monitoring are advised if tivozanib is prescribed with orally administered drugs that are sensitive substrates for BCRP, such as rosuvastatin.', NULL, 'Caution and clinical monitoring are advised if tivozanib is prescribed with orally administered drugs that are sensitive substrates for BCRP, such as rosuvastatin.  If coadministration is necessary, the manufacturer suggests separating the administration times by 2 or more hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/256860/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Fotivda (tivozanib)." Aveo Pharmaceuticals, Inc.  (2021):', 1767365210);
