INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239708, 'Boceprevir', 'Valdecoxib', 'Moderate', 'Coadministration with boceprevir may increase the plasma concentrations of drugs that are metabolized by CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by boceprevir.', NULL, 'Caution is advised if boceprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if boceprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever boceprevir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258056/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239709, 'Bromfenac', 'Valdecoxib', 'Major', 'Concomitant use of COX-2 inhibitors with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation.', NULL, 'Concomitant use of COX-2 inhibitors with non-aspirin NSAIDs should generally be avoided.', NULL, 'Concomitant use of COX-2 inhibitors with non-aspirin NSAIDs should generally be avoided.  Some authorities consider this combination to be contraindicated due to the absence of any evidence demonstrating synergistic benefits and the potential for additive adverse reactions (AU,UK).', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258057/', '[1] "Product Information. Celebrex (celecoxib)." Searle  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc "ANVISA Bulio Eletrico." O 0 (2015):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239710, 'Cinoxacin', 'Valdecoxib', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use.  The interaction has been reported most often with enoxacin.  It may occur with other fluoroquinolones as well, but is poorly documented.  The exact mechanism of interaction is unknown.  Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect.  Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258058/', '[1] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986):  187-93[2] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985):  716-21[3] "Product Information. Cipro (ciprofloxacin)." Bayer  (2002):[4] "Product Information. Penetrex (enoxacin)." Rhone Poulenc Rorer  (2002):[5] "Product Information. Floxin (ofloxacin)." Ortho McNeil Pharmaceutical  (2001):[6] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994):  685-706[7] "Product Information. Levaquin (levofloxacin)." Ortho McNeil Pharmaceutical  (2001):[8] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome  (2001):[9] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988):  97-107[10] Ball P, Tillotson G "Tolerability of fluoroquinolone antibiotics: past, present and future." Drug Saf 13 (1996):  343-8[11] "Product Information. Avelox (moxifloxacin)." Bayer  (2001):[12] "Product Information. Tequin (gatifloxacin)." Bristol-Myers Squibb  (2001):[13] "Product Information. Factive (gemifloxacin)." *GeneSoft Inc  (2003):[14] Segev S. Rehavi M, Rubinstein E "Quinolones, theophylline, and diclofenac interactions with the gamma-aminobutyric acid receptor." Antimicrob Agents Chemother 32 (1988):  1624-6', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239711, 'Danaparoid', 'Valdecoxib', 'Moderate', 'Anticoagulants, antiplatelet agents, and thrombolytics may potentiate the risk of gastrointestinal bleeding complications associated with the use of nonsteroidal anti-inflammatory drugs (NSAIDs) including COX-2 inhibitors.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.  Gastroprotective agents such as H2-receptor antagonists or proton pump inhibitors may be appropriate in high risk patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258062/', '[1] "Product Information. Celebrex (celecoxib)." Searle  (2001):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239712, 'Dicoumarol', 'Valdecoxib', 'Moderate', 'Coadministration with valdecoxib may increase the plasma concentrations and hypoprothrombinemic effect of warfarin and other oral anticoagulants.  The exact mechanism of interaction is unknown, although it is possible that valdecoxib partially inhibits the metabolic clearance of warfarin via CYP450 2C9 and 2C19.', NULL, 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and valdecoxib or parecoxib.', NULL, 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and valdecoxib or parecoxib.  The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of valdecoxib in patients who are stabilized on their anticoagulant regimen.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258063/', '[1] "Product Information. Bextra (valdecoxib)." Pharmacia and Upjohn  (2001):', 1767365210);
