INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239732, 'Valdecoxib', 'Trichlormethiazide', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states.  The risk may be increased in patients on dietary sodium restriction.  At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure.  Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination.', NULL, 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', NULL, 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.  If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/258101/', '[1] Allan SG, Knox J, Kerr F "Interaction between diuretics and indomethacin." Br Med J 283 (1981):  1611[2] McCarthy JT, Torres VE, Romero JC, et al. "Acute intrinsic renal failure induced by indomethacin." Mayo Clin Proc 57 (1982):  289-96[3] Favre L, Glasson P, Vallotton MB "Reversibl