INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (239948, 'Boceprevir', 'Troleandomycin', 'Moderate', 'Coadministration of a macrolide or ketolide antibiotic (clarithromycin, erythromycin, telithromycin) with a hepatitis C virus (HCV) NS3/4A protease inhibitor (boceprevir, telaprevir) is expected to increase the plasma concentrations of both drugs.  The mechanism may involve both competitive and noncompetitive inhibition of CYP450 3A4, since these drugs are all substrates as well as inhibitors of the isoenzyme.', NULL, 'Caution and close clinical monitoring are recommended if clarithromycin, erythromycin, or telithromycin are used in combination with a HCV NS3/4A protease inhibitor.', NULL, 'Caution and close clinical monitoring are recommended if clarithromycin, erythromycin, or telithromycin are used in combination with a HCV NS3/4A protease inhibitor.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/259139/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals  (2011):', 1767365210);
