        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1467555, 'Aluminum hydroxide', 'Nilotinib', 'Moderate', 'INTERVAL: Antacids significantly decrease the oral bioavailability of nilotinib. The mechanism is decreased solubility of nilotinib due to antacid-induced gastric pH increases. Nilotinib''s solubility is pH-dependent and it is insoluble at a pH of 4.5 or higher.', NULL, 'Patients treated with nilotinib should not take antacids or other agents with acid-neutralizing effects for at least 2 hours before or 2 hours after a nilotinib dose.', NULL, 'Patients treated with nilotinib should not take antacids or other agents with acid-neutralizing effects for at least 2 hours before or 2 hours after a nilotinib dose.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
