        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1467556, 'Aminoglutethimide', 'Nilotinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of nilotinib, which is primarily metabolized by the isoenzyme.', NULL, 'Patients receiving nilotinib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to nilotinib.', NULL, 'Patients receiving nilotinib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to nilotinib. Concomitant use should be avoided if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
