INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240156, 'Canakinumab', 'Lisocabtagene maraleucel', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections.  Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy.  Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents (greater than 10 mg/day to 1 mg/kg/day, whichever is less, of prednisone or equivalent for more than 2 weeks), and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.  Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.  Interleukin-1 blockers should be discontinued if neutropenia or serious infection occurs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/259782/', '[1] "Product Information. Kineret (anakinra)." Amgen  (2001):[2] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc  (2008):[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals  (2009):', 1767365210);
