INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240226, 'Ceritinib', 'Bromocriptine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of bromocriptine.  Orally administered bromocriptine is extensively metabolized in the gastrointestinal tract and liver by CYP450 3A4, with approximately 93% of the absorbed dose undergoing first-pass metabolism and only the remaining 7% reaching systemic circulation.', NULL, 'Concomitant use of bromocriptine with potent CYP450 3A4 inhibitors should be avoided when possible.', NULL, 'Concomitant use of bromocriptine with potent CYP450 3A4 inhibitors should be avoided when possible.  In patients who have been treated with a potent CYP450 3A4 inhibitor, an adequate washout of the inhibitor (approximately 3 to 5 elimination half-lives) is recommended prior to initiation of bromocriptine.  If coadministration with a potent CYP450 3A4 inhibitor is required, bromocriptine dosage should be reduced to avoid toxicity.  Caution and close monitoring for development of adverse effects are advisable, and the bromocriptine dosage further adjusted if necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/260355/', '[1] Nelson MV, Berchou RC, Kareti D, Le Witt PA "Pharmacokinetic evaluation of erythromycin and caffeine administered with bromocriptine." Clin Pharmacol Ther 47 (1990):  694-7[2] "Product Information. Parlodel (bromocriptine)." Sandoz Pharmaceuticals Corporation  (2001):[3] von Rosenstiel NA, Adam D "Macrolide antibacterials. Drug interactions of clinical significance." Drug Saf 13 (1995):  105-22[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Periti P, Mazzei T, Mini E, Novelli A "Pharmacokinetic drug interactions of macrolides." Clin Pharmacokinet 23 (1992):  106-31[7] "Product Information. Cycloset (bromocriptine)." Valeant Pharmaceuticals  (2018):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240227, 'Chloramphenicol', 'Bromocriptine', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of bromocriptine.  Orally administered bromocriptine is extensively metabolized in the gastrointestinal tract and liver by CYP450 3A4, with approximately 93% of the absorbed dose undergoing first-pass metabolism and only the remaining 7% reaching systemic circulation.  As such, inhibitors of CYP450 3A4 may markedly reduce the metabolic clearance of bromocriptine.', NULL, 'Caution and close monitoring for development of adverse effects are advisable during coadministration of bromocriptine with moderate CYP450 3A4 inhibitors.', NULL, 'Caution and close monitoring for development of adverse effects are advisable during coadministration of bromocriptine with moderate CYP450 3A4 inhibitors.  Bromocriptine dosage may need to be reduced to avoid toxicity.  The prescribing information for Cycloset, a bromocriptine product indicated for use in adults with type 2 diabetes mellitus to improve glycemic control, recommends limiting the dose to 1.6 mg daily during concomitant use of a moderate CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/260356/', '[1] Nelson MV, Berchou RC, Kareti D, Le Witt PA "Pharmacokinetic evaluation of erythromycin and caffeine administered with bromocriptine." Clin Pharmacol Ther 47 (1990):  694-7[2] "Product Information. Parlodel (bromocriptine)." Sandoz Pharmaceuticals Corporation  (2001):[3] von Rosenstiel NA, Adam D "Macrolide antibacterials. Drug interactions of clinical significance." 