INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240488, 'Dexmethylphenidate', 'Verapamil', 'Moderate', 'Methylphenidate and other derivative amphetamines may decrease the therapeutic effects of antihypertensive drugs.', NULL, 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.', NULL, 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.  The dosage of the antihypertensive drug should be adjusted as needed.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261187/', '[1] "Product Information. Ritalin (methylphenidate)." Novartis Pharmaceuticals  (2001):[2] "Product Information. Metadate CD (methylphenidate)." Celltech Pharmaceuticals Inc  (2022):[3] "Product Information. Focalin (dexmethylphenidate)." Mikart Inc  (2001):[4] "Product Information. Metadate ER (methylphenidate)." Celltech Pharmaceuticals Inc  (2002):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Ritalin LA (methylphenidate)." Quality Care Products/Lake Erie Medical  (2013):[8] "Product Information. Azstarys (dexmethylphenidate-serdexmethylphenidate)." Corium, Inc.  (2021):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240489, 'Drospirenone', 'Verapamil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of drospirenone, which is partially metabolized by the isoenzyme.', NULL, 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.', NULL, 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.g., renal insufficiency; coadministration with medications that can increase potassium such as aldosterone antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium-sparing diuretics, potassium supplements, and heparin).  Consider checking serum potassium levels during the first treatment cycle and periodically thereafter as indicated.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261188/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Slynd (drospirenone)." Exeltis USA, Inc.  (2022):[3] "Product Information. Angeliq (drospirenone-estradiol)." Bayer Pharmaceutical Inc  (2017):[4] "Product Information. Slinda (drospirenone)." Besins Healthcare Australia Pty Ltd  (2021):[5] "Product Information. Angeliq 1/2 (drospirenone-estradiol)." Bayer Australia Limited  (2021):[6] "Product Information. Slynd (drospirenone)." Duchesnay Inc  (2021):[7] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb  (2022):[8] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Australia Pty Ltd  (2023):[9] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Pharmaceuticals Ltd  (2021):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240490, 'Verapamil', 'Melatonin', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may substantially increase the plasma concentrations of melatonin.  Data from available studies suggest that melatonin is primarily metabolized by CYP450 1A2, with possible contribution from CYP450 2C19 and 2C9.  Coadministration of fluvoxamine, a potent CYP450 1A2 inhibitor that also inhibits CYP450 2C19 and 2C9, increased melatonin systemic exposure (AUC) by 17-fold and maximum plasma concentration (Cmax) by 12-fold.  Contraceptives containing ethinyl estradiol, which can inhibit CYP450 1A2, may lead to a 4- to 5-fold increase in melatonin concentration.', NULL, 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.', NULL, 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.  Patients should be monitored closely for adverse effects such as excessive drowsiness, headache, lethargy, dizziness, irritability, nervousness, restlessness, anxiety, abdominal pain, and dyspepsia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261204/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240491, 'Verapamil', 'Pyrophosphoric acid', 'Moderate', 'Medicinal products including sodium heparin, prazosin, methyldopa, hydralazine, quinidine, beta blockers, calcium channel blockers, digitalis glycosides, nitrates, anthracyclines, and iodinated contrast agents may interfere with the blood pool images taken using Technetium Tc 99m pyrophosphate.  This is characterized by a reduction in the amount of injected radioactivity remaining in the blood pool.', NULL, 'Clinicians should be aware of the possibility of a diagnostic interference.', NULL, 'Clinicians should be aware of the possibility of a diagnostic interference.  When use of iodinated contrast media is anticipated, it is recommended to perform the scintigraphy with (99mTc)-labeled red blood cells prior to administration of the iodinated contrast media to avoid adversely affecting the red blood cell labeling.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261214/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Pyrophosphate (pyrophosphate)." Pharmalucence Inc  (2012):', 1767365210);
