INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240497, 'Ginkgo biloba', 'Diclofenac', 'Moderate', 'Ginkgo may potentiate the risk of bleeding associated with anticoagulants, platelet inhibitors, nonsteroidal anti-inflammatory drugs (NSAIDs), and thrombolytic agents.  Ginkgolide B, a component of ginkgo, inhibits platelet-activating factor by displacing it from its receptor-binding site, resulting in reduced platelet aggregation.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.  In general, consumption of ginkgo should be avoided during use of coagulation-modifying agents and at least two weeks prior to surgery.  In patients who have used this herb extensively prior to receiving anticoagulation, antiplatelet, NSAID or thrombolytic therapy, the potential for an interaction should be considered.  Close clinical and laboratory observation for hematologic complications is recommended.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261297/', '[1] Miller LG "Herbal medicinals: selected clinical considerations focusing on known or potential drug-herb interactions." Arch Intern Med 158 (1998):  2200-11[2] Rosenblatt M, Mindel J "Spontaneous hyphema associated with ingestion of Ginkgo biloba extract." N Engl J Med 336 (1997):  1108[3] Rowin J, Lewis SL "Spontaneous bilateral subdural hematomas associated with chronic Gingko biloba ingestion." Neurology 46 (1996):  1775-6[4] Chung KF, McCusker M, Page CP, Dent G, Guinot P, Barnes PJ "Effect of ginkgolide mixture (BN 52063) in antagonising skin and platelet responses to platelet acitivating factor in man." Lancet 1 (1987):  248-51[5] Fugh-Berman A "Herb-drug interactions." Lancet 355 (2000):  134-8[6] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000):  1221-7; quiz 1228-30[7] Vaes LP, Chyka PA "Interactions of warfarin with garlic, ginger, or ginseng: nature of evidence." Ann Pharmacother 34 (2000):  1478-82[8] Fessenden JM, Wittenborn W, Clarke L "Gingko biloba: A case report of herbal medicine and bleeding postoperatively from a laparoscopic cholecystectomy." Am Surg 67 (2001):  33-5[9] Benjamin J, Muir T, Briggs K, Pentland B "A case of cerebral haemorrhage - can Ginkgo biloba be implicated?." Postgrad Med J 77 (2001):  112-3[10] Izzo AA, Ernst E "Interactions between herbal medicines and prescribed drugs: a systematic review." Drugs 61 (2001):  2163-75[11] Evans V "Herbs and the brain: friend or foe? The effects of ginkgo and garlic on warfarin use." J Neurosci Nurs 32 (2000):  229-32[12] Cupp MJ "Herbal remedies: adverse effects and drug interactions." Am Fam Physician 59 (1999):  1239-45[13] Matthews MK Jr "Association of Ginko biloba with intracerebral hemorrhage." Neurology 50 (1998):  1933-4[14] Engelsen J, Nielsen JD, Winther K "Effect of coenzyme Q10 and Ginkgo biloba on warfarin dosage in stable, long-term warfarin treated outpatients. A randomised, double blind, placebo-crossover trial." Thromb Haemost 87 (2002):  1075-6[15] Fong KC, Kinnear PE "Retrobulbar haemorrhage associated with chronic Gingko biloba ingestion." Postgrad Med J 79 (2003):  531-2[16] Sierpina VS, Wollschlaeger B, Blumenthal M "Ginkgo biloba." Am Fam Physician 68 (2003):  923-6[17] Jiang X, Williams KM, Liauw WS, et al. "Effect of ginkgo and ginger on the pharmacokinetics and pharmacodynamics of warfarin in healthy subjects." Br J Clin Pharmacol 59 (2005):  425-32[18] Jayasekera N, Moghal A, Kashif F, Karalliedde L "Herbal medicines and postoperative haemorrhage." Anaesthesia 60 (2005):  725-6[19] Wolf HR "Does Ginkgo biloba special extract EGb 761 provide additional effects on coagulation and bleeding when added to acetylsalicylic acid 500 mg daily?" Drugs R D 7 (2006):  163-72', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240498, 'Heparin (flush)', 'Diclofenac', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', NULL, 'Caution and monitoring (e.', NULL, 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261298/', '[1] Passannante A, Macik BG "The heparin flush syndrome: a cause of iatrogenic hemorrhage." Am J Med Sci 296 (1988):  71-3[2] "Product Information. Heparin Sodium in Sodium Chloride (heparin flush)." Baxter Healthcare Corporation  (2022):[3] "Product Information. Canusal (heparin flush)." Wockhardt UK Ltd  (2018):[4] "Product Information. Heparinised Saline (Pfizer) (heparinised saline)." Pfizer Australia Pty Ltd  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240499, 'Diclofenac', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261315/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240500, 'Diclofenac', 'Turmeric', 'Moderate', 'Turmeric may potentiate the bleeding risk associated with drugs that interfere with platelet function or coagulation.', NULL, 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.', NULL, 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.  Clinical and laboratory observation for hematologic complications is recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/261320/', '[1] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000):  1221-7; quiz 1228-30[2] Abebe W "Herbal medication: potential for adverse interactions with analgesic drugs." J Clin Pharm Ther 27 (2002):  391-401[3] Yang X, Thomas DP, Zhang X, et al. "Curcumin inhibits platelet-derived growth factor-stimulated vascular smooth muscle cell function and injury-induced neointima formation." Arterioscler Thromb Vasc Biol 26 (2006):  85-90[4] New Zealand Medicines and Medical Devices Safety Authority "Medsafe Monitoring Communication: Beware turmeric/curcumin containing products can interact with warfarin.  https://medsafe.govt.nz/safety/EWS/2018/Turmeric.asp"   (2022):', 1767365210);
