        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1467756, 'Raltegravir', 'Nilotinib', 'Minor', 'Coadministration with inhibitors of uridine diphosphate glucuronosyltransferase (UGT) 1A1 such as atazanavir, nilotinib, or sorafenib may increase the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. However, concurrent use of atazanavir/ritonavir in clinical trials did not result in meaningful differences in the safety of raltegravir. Based on these data, no dosage adjustment of raltegravir is necessary when used together with atazanavir/ritonavir or other UGT1A1 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
