        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1467757, 'Ranitidine (bismuth citrate)', 'Nilotinib', 'Moderate', 'INTERVAL: Coadministration of H2 antagonists may significantly decrease the oral bioavailability of nilotinib. The mechanism is decreased solubility of nilotinib due to increased gastric pH. Nilotinib''s solubility is pH dependent and it is insoluble at a pH of 4.5 or higher.', NULL, 'During concomitant use, it is recommended to administer the H2 antagonist 10 hours before and 2 hours after the dose of nilotinib.', NULL, 'During concomitant use, it is recommended to administer the H2 antagonist 10 hours before and 2 hours after the dose of nilotinib.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
