    ) VALUES (1468215, 'Troleandomycin', 'Nimodipine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations and blood pressure lowering effect of nimodipine, which is primarily metabolized by CYP450 3A4 in the intestinal mucosa and liver.', NULL, 'The use of nimodipine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated because of a risk of significant hypotension.', NULL, 'The use of nimodipine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated because of a risk of significant hypotension.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
