INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240652, 'Diltiazem', 'Pyrophosphoric acid', 'Moderate', 'Medicinal products including sodium heparin, prazosin, methyldopa, hydralazine, quinidine, beta blockers, calcium channel blockers, digitalis glycosides, nitrates, anthracyclines, and iodinated contrast agents may interfere with the blood pool images taken using Technetium Tc 99m pyrophosphate.  This is characterized by a reduction in the amount of injected radioactivity remaining in the blood pool.', NULL, 'Clinicians should be aware of the possibility of a diagnostic interference.', NULL, 'Clinicians should be aware of the possibility of a diagnostic interference.  When use of iodinated contrast media is anticipated, it is recommended to perform the scintigraphy with (99mTc)-labeled red blood cells prior to administration of the iodinated contrast media to avoid adversely affecting the red blood cell labeling.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/262401/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Pyrophosphate (pyrophosphate)." Pharmalucence Inc  (2012):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240653, 'Doxazosin', 'Osilodrostat', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', NULL, 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/262427/', '[1] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Inc  (2020):[2] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Australia Pty Ltd  (2022):[3] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases UK Ltd  (2021):', 1767365210);
