INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240830, 'Yellow Fever Vaccine', 'Ocrelizumab', 'Major', 'The administration of live, attenuated virus or bacterial vaccines during immunosuppressant therapy with ocrelizumab may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.  Additionally, there is a lack of data on the safety of immunization following discontinuation of ocrelizumab.  Ocrelizumab use causes a depletion of B-cells and can be expected to similarly increase the risk of a decreased or suboptimal immunologic vaccine response if immunization occurs before B-cells counts have recovered', NULL, 'Live virus or bacterial vaccines should not be administered to patients during therapy or after discontinuing ocrelizumab therapy until recovery from B-cell depletion occurs.', NULL, 'Live virus or bacterial vaccines should not be administered to patients during therapy or after discontinuing ocrelizumab therapy until recovery from B-cell depletion occurs.  Immunization status should be assessed prior to initiating ocrelizumab and recommended immunization with live or attenuated live vaccines should be completed at least 4 weeks prior to ocrelizumab initiation (some authorities have recommended at least 6 weeks).  For infants of mothers exposed to ocrelizumab during pregnancy, live or live-attenuated vaccines should be delayed until B-cell levels have recovered; therefore, measuring CD19 positive B-cell levels prior to vaccination in these infants is recommended.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263323/', '[1] Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):[2] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993):  1-18[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Ocrevus (ocrelizumab)." Genentech  (2017):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240831, 'Yellow Fever Vaccine', 'Olaparib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263324/', '[1] "Product Information. Meruvax II (rubella virus vaccine)." Merck & Co., Inc  (2022):[2] "Product Information. Attenuvax (measles virus vaccine)." Merck & Co., Inc  (2022):[3] "Product Information. YF-Vax (yellow fever vacc