INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240948, 'Sulfinpyrazone', 'Penbutolol', 'Minor', 'Sulfinpyrazone may reduce the antihypertensive effect of beta-blockers.  The mechanism may be related to induction of beta-blocker metabolism or inhibition of prostaglandin synthesis by sulfinpyrazone.', NULL, 'Close observation for altered beta-blocker effects is recommended if coadministration is necessary.', NULL, 'Close observation for altered beta-blocker effects is recommended if coadministration is necessary.', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263682/', '[1] Ferrara LA, Mancini M, Marotta T, Pasanisi F, Fasano ML "Interference by sulphinpyrazone with the antihypertensive effects of oxprenolol." Eur J Clin Pharmacol 29 (1986):  717-9', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240949, 'Sulfinpyrazone', 'Phenylbutazone', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.  Close clinical and laboratory observation for hematologic complications is recommended.  Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263683/', '[1] "Product Information. Acular (ketorolac)." Allergan Inc  (2001):[2] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals  (2005):[3] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc  (2005):', 1767365210);
