INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240951, 'Sulfinpyrazone', 'Relugolix', 'Minor', 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.  In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8.', NULL, 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.', NULL, 'Coadministration with lone inducers of CYP450 3A4 is unlikely to decrease the plasma concentrations of relugolix to a clinically significant extent.  In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8.  Relugolix is also a substrate for intestinal P-gp.  When relugolix was coadministered with rifampin, a combined P-gp and potent CYP450 3A inducer, relugolix peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 23% and 55%, respectively.  By contrast, no clinically significant differences in the pharmacokinetics of relugolix were observed when coadministered with enzalutamide, a strong CYP450 3A inducer that is not known to induce P-gp.', 'Metabolism, Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263686/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc.  (2021):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240952, 'Sulfinpyrazone', 'Rofecoxib', 'Moderate', 'Anticoagulants, antiplatelet agents, and thrombolytics may potentiate the risk of gastrointestinal bleeding complications associated with the use of nonsteroidal anti-inflammatory drugs (NSAIDs) including COX-2 inhibitors.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.  Gastroprotective agents such as H2-receptor antagonists or proton pump inhibitors may be appropriate in high risk patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263687/', '[1] "Product Information. Celebrex (celecoxib)." Searle  (2001):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240953, 'Telaprevir', 'Sulfinpyrazone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, which are metabolized by the isoenzyme.', NULL, 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer.', NULL, 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer.  Close clinical monitoring of the virologic response to the HCV NS3/4A protease inhibitor is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263690/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals  (2011):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240954, 'Tinzaparin', 'Sulfinpyrazone', 'Major', 'Drugs that can affect hemostasis such as dextran, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or other anticoagulants may potentiate the risk of bleeding complications associated with the use of a low molecular weight heparin (LMWH), heparinoid, or fondaparinux.', NULL, 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy.', NULL, 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy.  If coadministration is necessary, it should be undertaken with caution and only after thorough assessment of risks and benefits.  Close clinical and laboratory observation for bleeding complications is recommended.  Patients undergoing neuraxial intervention and treated with these agents should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.  The optimal timing between the administration of anticoagulants and neuraxial procedures is not known.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263692/', '[1] "Product Information. Lovenox (enoxaparin)." Rhone Poulenc Rorer  (2002):[2] "Product Information. Fragmin (dalteparin)." Pharmacia and Upjohn  (2001):[3] Price AJ, Frcpath DO "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995):  395[4] "Product Information. Orgaran (danaparoid)." Organon  (2001):[5] "Product Information. Normiflo (ardeparin)." Wyeth-Ayerst Laboratories  (2001):[6] "Product Information. Innohep (tinzaparin)." DuPont Pharmaceuticals  (2001):[7] "Product Information. Arixtra (fondaparinux)." Organon  (2002):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240955, 'Tositumomab', 'Sulfinpyrazone', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.  Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.  A complete blood count (CBC) with differential and platelet count should be obtained prior to and at least weekly for a minimum of 10 weeks after treatment.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263693/', '[1] "Product Information. Bexxar I 131 Therapeutic (iodine I 131 tositumomab)." GlaxoSmithKline  (2022):[2] "Product Information. BexxarTherapy (tositumomab)." GlaxoSmithKline Inc  (2006):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240956, 'Tramadol', 'Sulfinpyrazone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tramadol, which is primarily metabolized by CYP450 3A4 and 2D6.  Reduced opioid efficacy can occur, including opioid withdrawal in physically dependent patients.', NULL, 'Caution is advised when tramadol is used with CYP450 3A4 inducers.', NULL, 'Caution is advised when tramadol is used with CYP450 3A4 inducers.  Patients should be monitored for opioid efficacy and signs of withdrawal.  If a CYP450 3A4 inducer is discontinued, monitor for seizures, serotonin syndrome, sedation, and respiratory depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263694/', '[1] "Product Information. Seglentis (celecoxib-tramadol)." Kowa Pharmaceuticals America (formerly ProEthic)  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240957, 'Urokinase', 'Sulfinpyrazone', 'Moderate', 'Drugs that inhibit platelet function may increase the risk of bleeding when administered prior to, during, or after thrombolytic therapy.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin.  Some authorities recommend avoiding the initiation of platelet aggregation inhibitors within the first 24 hours following thrombolysis treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263695/', '[1] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc  (2001):[2] "Product Information. Abbokinase (urokinase)." Abbott Pharmaceutical  (2001):[3] "Product Information. Activase (alteplase)." Genentech  (2001):[4] "Product Information. Streptase (streptokinase)." Astra-Zeneca Pharmaceuticals  (2001):[5] "Product Information. Retavase (reteplase)." Boehringer Mannheim  (2001):[6] "Product Information. TNKase (tenecteplase)." Genentech  (2001):[7] Harder S, Klinkhardt U "Thrombolytics: drug interactions of clinical significance." Drug Saf 23 (2000):  391-9[8] Hirsch J, Dalen J, Guyatt G, American College of Chest Physicians "The sixth (2000) ACCP guidelines for antithrombotic therapy for prevention and treatment of thrombosis. American College of Physicians." Chest 119(1 Suppl) (2001):  1S-2S', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240958, 'Darolutamide', 'Sunitinib', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.  Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263700/', '[1] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc  (2002):[2] "Product Information. Zoladex (goserelin)." Astra-Zeneca Pharmaceuticals  (2001):[3] "Product Information. Trelstar (triptorelin)." Pharmacia and Upjohn  (2001):[4] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals  (2002):[5] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc  (2003):[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc)  (2010):[8] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc  (2013):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240959, 'Heparin (flush)', 'Sunitinib', 'Moderate', 'Concomitant use of heparin flush and drugs that interfere with platelet function or coagulation or drugs that commonly cause thrombocytopenia may potentiate the risk of bleeding complications.  Although uncommon, inadvertent anticoagulation resulting in hemorrhagic events may occur with heparin flushes, particularly during repeated or excessive use or administration of higher concentration flush solutions.', NULL, 'Caution and monitoring (e.', NULL, 'Caution and monitoring (e.g., platelet counts, hematocrits, tests for occult blood in stools) are advised when heparin flushes are given to patients receiving systemic anticoagulants, platelet inhibitors, antithrombotic agents, thrombolytics, or drugs with known risks of thrombocytopenia or bleeding.  An unexplained fall in hematocrit, fall in blood pressure, or any other unexplained symptom should lead to serious consideration of a hemorrhagic event.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263702/', '[1] Passannante A, Macik BG "The heparin flush syndrome: a cause of iatrogenic hemorrhage." Am J Med Sci 296 (1988):  71-3[2] "Product Information. Heparin Sodium in Sodium Chloride (heparin flush)." Baxter Healthcare Corporation  (2022):[3] "Product Information. Canusal (heparin flush)." Wockhardt UK Ltd  (2018):[4] "Product Information. Heparinised Saline (Pfizer) (heparinised saline)." Pfizer Australia Pty Ltd  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240960, 'Ibrutinib', 'Sunitinib', 'Major', 'Coadministration of ibrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ibrutinib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ibrutinib.  Close clinical and laboratory observation for bleeding complications is recommended during therapy.  The INR should be monitored more frequently during coadministration of warfarin.  In addition, some authorities recommend avoiding the use of supplements that have an inhibitory effect on platelet function such as fish oil (omega-3 polyunsaturated fatty acids), flaxseed, and vitamin E preparations (AU, CA, UK) because of an increased risk of bleeding.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263703/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Agencia Espala de Medicamentos y Productos Sanitarios Healthcare "Centro de informaci online de medicamentos de la AEMPS - CIMA.  https://cima.aemps.es/cima/publico/home.html"   (2008):[3] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc  (2013):[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (240961, 'Sunitinib', 'Pralsetinib', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/263712/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767365210);
