INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241007, 'Tolevamer', 'Tapentadol', 'Moderate', 'Coadministration with medications that can cause constipation such as opioids may increase the risk of intestinal injuries associated with the use of sodium polystyrene sulfonate.', NULL, 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.', NULL, 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.  The prescribing information recommends avoiding the use of sodium polystyrene sulfonate in patients who are at risk for developing constipation or impaction, including those with a history of impaction, chronic constipation, inflammatory bowel disease, ischemic colitis, vascular intestinal atherosclerosis, previous bowel resection, or bowel obstruction, as well as those who have not had a bowel movement post-surgery.  If clinically significant constipation develops, treatment with sodium polystyrene sulfonate should be discontinued until normal bowel motion is resumed.  Concomitant administration of sorbitol is not recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/264134/', '[1] Foresti V "Intestinal obstruction due to kayexalate in a patient concurrently treated with aluminum hydroxide and morphine sulfate." Clin Nephrol 41 (1994):  252[2] "Product Information. Kayexalate (sodium polystyrene sulfonate)." Sanofi Winthrop Pharmaceuticals  (2001):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241008, 'Bacillus calmette-guerin substrain tice live antigen', 'Nilotinib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/264136/', '[1] "Product Information. Meruvax II (rubella virus vaccine)." Merck & Co., Inc  (2022):[2] "Product Information. Attenuvax (measles virus vaccine)." Merck & Co., Inc  (2022):[3] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur  (2001):[4] Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):[5] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993):  1-18[6] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Co., Inc  (2002):[7] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011):  1495-7[8] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014):  161[9] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence.  https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf"   (2019):[10] "Product Information. DENGVAXIA (dengue vaccine)." sanofi pasteur  (2022):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241009, 'Drospirenone', 'Nilotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of drospirenone, which is partially metabolized by the isoenzyme.', NULL, 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.', NULL, 'Caution and close clinical monitoring are recommended when drospirenone is prescribed with CYP450 3A4 inhibitors, particularly in patients requiring long-term concomitant use and those with other risk factors for hyperkalemia (e.g., renal insufficiency; coadministration with medications that can increase potassium such as aldosterone antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium-sparing diuretics, potassium supplements, and heparin).  Consider checking serum potassium levels during the first treatment cycle and periodically thereafter as indicated.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/264147/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation  (2011):[2] "Product Information. Slynd (drospirenone)." Exeltis USA, Inc.  (2022):[3] "Product Information. Angeliq (drospirenone-estradiol)." Bayer Pharmaceutical Inc  (2017):[4] "Product Information. Slinda (drospirenone)." Besins Healthcare Australia Pty Ltd  (2021):[5] "Product Information. Angeliq 1/2 (drospirenone-estradiol)." Bayer Australia Limited  (2021):[6] "Product Information. Slynd (drospirenone)." Duchesnay Inc  (2021):[7] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb  (2022):[8] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Australia Pty Ltd  (2023):[9] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb Pharmaceuticals Ltd  (2021):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241010, 'Pralsetinib', 'Nilotinib', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/264185/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241011, 'Varicella Zoster Vaccine (Recombinant)', 'Nilotinib', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition.  Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents (e.g., greater than or equal to 2 mg/kg/day or 20 mg/day of prednisone or equivalent for 14 consecutive days or more), or long-term topical or inhaled corticosteroids.', NULL, 'In general, the U.', NULL, 'In general, the U.S. Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.  However, higher dosages, more frequent boosters, and/or serological testing may be required in some cases.  Local guidelines and prescribing information for individual vaccines should be consulted.  For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease.  For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.  For SARS-CoV-2 (COVID-19) vaccines, vaccination should preferably be completed at least two weeks before initiation or resumption of immunosuppressive therapies; however, decisions to delay or temporarily withhold immunosuppressive therapy to complete COVID-19 vaccination should consider the individual''s risks relative to their underlying condition.  Additional shots and/or boosters may be appropriate depending on age, prior COVID-19 vaccine formulation(s) received, and other factors in individuals with moderate to severe immune compromise due to medical conditions or immunosuppressive medications or treatments (e.g., solid organ transplant recipients on immunosuppressive therapy; patients on active treatment for solid tumor and hematologic malignancies).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/264219/', '[1] "Product Information. Fluzone (influenza virus vaccine, inactivated)." Connaught Laboratories Inc[2] "Product Information. Omnihib (haemophilus b conjugate vaccine (obsolete))." SmithKline Beecham[3] "Product Information. Havrix (hepatitis A adult vaccine)." SmithKline Beecham[4] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993):  1-18[5] "Product Information. Imovax Rabies (rabies vaccine, human diploid cell)." sanofi pasteur  (2022):[6] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc.  (2003):[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline  (2022):[9] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence.  https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf"   (2019):[10] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations.  https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chapter_6.pdf"   (2019):[11] CDC Centers for Disease Control and Prevention "Interim Clinical Considerations for Use of COVID-19 Vaccines Currently Approved or Authorized in the United States.  https://www.cdc.gov/vaccines/covid-19/clinical-considerations/interim-considerations-us.html"   (2022):', 1767363028);
