INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241255, 'Tucatinib', 'Ospemifene', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of ospemifene, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors.  Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain, shortness of breath, and pain or swelling in the arms or legs.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/266464/', '[1] "Product Information. Osphena (ospemifene)." Shionogi USA Inc  (2013):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241256, 'Tucatinib', 'Propafenone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of propafenone, which may result in proarrhythmic events and exaggerated beta-adrenergic blocking activity.', NULL, 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors.  Therapeutic response to propafenone as well as clinical and electrocardiographic evidence of toxicity should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the propafenone dosage adjusted as necessary.  Patients should be monitored for potential adverse effects such as new or worsening arrhythmias or heart failure, edema, bradycardia, and atrioventricular block.  Simultaneous use of propafenone with both a CYP450 3A4 inhibitor and a CYP450 2D6 inhibitor (or in patients with CYP450 2D6 deficiency) should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/266466/', '[1] Botsch S, Gautier JC, Beaune P, Eichelbaum M, Kroemer HK "Identification and characterization of the cytochrome P450 enzymes involved in N-dealkylation of propafenone: molecular base for interaction potential and variable disposition of active metabolites." Mol Pharmacol 43 (1993):  120-6[2] "Product Information. Rythmol SR (propafenone)." GlaxoSmithKline  (2011):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241257, 'Tucatinib', 'Tramadol', 'Moderate', 'Coadministration with drugs that are inhibitors of the CYP450 3A4 isoenzymes may increase the plasma concentrations of tramadol and its active O-demethylated metabolite.', NULL, 'Caution is advised if tramadol is used concomitantly with medications that inhibit CYP450 3A4 metabolism.', NULL, 'Caution is advised if tramadol is used concomitantly with medications that inhibit CYP450 3A4 metabolism.  Dosage adjustments as well as clinical monitoring may be appropriate whenever there drugs are added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/266473/', '[1] "Product Information. Ultram (tramadol)." McNeil Pharmaceutical  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241258, 'Docetaxel', 'Pexidartinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of docetaxel, which is a substrate of the isoenzyme.  Rifampin and hyperforin, a component of St. John''s wort, have been shown to significantly induce the in vitro metabolism of docetaxel in human hepatocyte cultures.', NULL, 'Coadministration of docetaxel with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'Coadministration of docetaxel with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of docetaxel underexposure.  Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.  If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised.  Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with docetaxel is unknown.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/266486/', '[1] "Product Information. Taxotere (docetaxel)." Rhone Poulenc Rorer  (2001):[2] Aronson JK, Grahame-Smith DG "Clinical pharmacology: adverse drug interactions." Br Med J 282 (1981):  288-91[3] McInnes GT, Brodie MJ "Drug interactions that matter: a critical reappraisal." Drugs 36 (1988):  83-110[4] Baker SD "Drug interactions with the taxanes." Pharmacotherapy 17(5 Pt 2) (1997):  s126-32[5] Komoroski BJ, Parise RA, Egorin MJ, Strom SC, Venkataramanan R "Effect of the St. John''s wort constituent hyperforin on docetaxel metabolism by human hepatocyte cultures." Clin Cancer Res 11(19 Pt 1) (2005):  6972-9', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241259, 'Pexidartinib', 'Macimorelin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of macimorelin, which is primarily metabolized by the isoenzyme.', NULL, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.', NULL, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.g., carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, St. John''s wort) as well as some moderate ones (e.g., bosentan, efavirenz, etravirine, modafinil) prior to macimorelin administration.  A sufficient washout period following discontinuation of the inducers is also advised before using macimorelin.  No recommendations are available for other, less potent CYP450 3A4 inducers; however, it may be appropriate to follow the same precaution.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/266499/', '[1] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241260, 'Pexidartinib', 'Neratinib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', NULL, 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided.', NULL, 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided.  According to some authorities, if the CYP450 3A4 inducer cannot be avoided, the dose of neratinib should be increased to 320 mg once daily.  The daily dose of neratinib should not exceed 320 mg.  The previous dose of neratinib may be resumed following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/266505/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc.  (2017):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241261, 'Tramadol', 'Pexidartinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tramadol, which is primarily metabolized by CYP450 3A4 and 2D6.  Reduced opioid efficacy can occur, including opioid withdrawal in physically dependent patients.', NULL, 'Caution is advised when tramadol is used with CYP450 3A4 inducers.', NULL, 'Caution is advised when tramadol is used with CYP450 3A4 inducers.  Patients should be monitored for opioid efficacy and signs of withdrawal.  If a CYP450 3A4 inducer is discontinued, monitor for seizures, serotonin syndrome, sedation, and respiratory depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/266515/', '[1] "Product Information. Seglentis (celecoxib-tramadol)." Kowa Pharmaceuticals America (formerly ProEthic)  (2022):', 1767363028);
