INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241472, 'Cidofovir', 'Moxetumomab Pasudotox', 'Major', 'Coadministration of cidofovir with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney.  Dose-related nephrotoxicity is the major toxicity of cidofovir.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, cisplatin, clofarabine, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravascular contrast media; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) is considered contraindicated.  Such agents should be discontinued for at least seven days prior to starting therapy with cidofovir.  Initiation of cidofovir is also contraindicated in patients with a serum creatinine >1.5 mg/dL, a calculated creatinine clearance <=55 mL/min, or a urine protein >=100 mg/dL (2+ proteinuria or greater).', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267732/', '[1] "Product Information. Vistide (cidofovir)." Gilead Sciences  (200