INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241493, 'Iron protein succinylate', 'Cinoxacin', 'Moderate', 'Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics.  Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc.  The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.  When coadministered with Suprep Bowel Prep (magnesium/potassium/sodium sulfates), the manufacturer recommends administering fluoroquinolone antibiotics at least 2 hours before and not less than 6 hours after Suprep Bowel Prep to avoid chelation with magnesium.  Please consult individual product labeling for specific recommendations.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/267811/', '[1] Polk RE, Helay DP, Sahai J, Drwal L, Racht E "Effect of ferrous sulfate and multivitamins with zinc on absorption of ciprofloxacin in normal volunteers." Antimicrob Agents Chemother 33 (1989):  1841-4[2] Nix DE, Watson WA, Lener ME, et al. "Effects of aluminum and magnesium antacids and ranitid