INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468303, 'Bepridil', 'Niraparib', 'Moderate', 'Coadministration with potent inhibitors of P-glycoprotein (P-gp) may increase the bioavailability of niraparib, which is a substrate of the efflux transporter. Increased levels of niraparib may lead to increased incidence of hematological toxicity (e.g., thrombocytopenia, anemia, neutropenia) and/or cardiovascular toxicity (e.g., hypertension, hypertensive crisis).', 'DDInter', 1767317476, 'Pharmacologic response to niraparib should be monitored more closely whenever a potent P-gp inhibitor is added to or withdrawn from therapy, and the niraparib dosage adjusted as necessary.', 'Absorption', 'Pharmacologic response to niraparib should be monitored more closely whenever a potent P-gp inhibitor is added to or withdrawn from therapy, and the niraparib dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468304, 'Bifidobacterium longum infantis', 'Niraparib', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
