INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468446, 'Telithromycin', 'Niraparib', 'Moderate', 'Coadministration with potent inhibitors of P-glycoprotein (P-gp) may increase the bioavailability of niraparib, which is a substrate of the efflux transporter. Increased levels of niraparib may lead to increased incidence of hematological toxicity (e.g., thrombocytopenia, anemia, neutropenia) and/or cardiovascular toxicity (e.g., hypertension, hypertensive crisis).', 'DDInter', 1767317476, 'Pharmacologic response to niraparib should be monitored more closely whenever a potent P-gp inhibitor is added to or withdrawn from therapy, and the niraparib dosage adjusted as necessary.', 'Absorption', 'Pharmacologic response to niraparib should be monitored more closely whenever a potent P-gp inhibitor is added to or withdrawn from therapy, and the niraparib dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
