INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468480, 'Vemurafenib', 'Niraparib', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters, such as dabigatran, rosuvastatin, sulfasazine, and topotecan. The proposed mechanism, based on in vitro data, is decreased clearance due to vemurafenib-mediated inhibition of P-gp and/or BCRP efflux transport proteins.', 'DDInter', 1767317476, 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of P-gp and/or BCRP transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever vemurafenib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Others', 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of P-gp and/or BCRP transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
