INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468529, 'Fluvoxamine', 'Nisoldipine', 'Moderate', 'Coadministration with fluvoxamine may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to competitive inhibition of CYP450 3A4 activity by fluvoxamine.', 'DDInter', 1767317476, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluvoxamine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468530, 'Fosamprenavir', 'Nisoldipine', 'Moderate', 'Coadministration with amprenavir or its prodrug, fosamprenavir, may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by amprenavir.', 'DDInter', 1767317476, 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever amprenavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468531, 'Fosaprepitant', 'Nisoldipine', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317476, 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468532, 'Fosinopril', 'Nisoldipine', 'Minor', 'Calcium channel blockers and angiotensin converting enzyme (ACE) inhibitors may have additive hypotensive effects. While these drugs are often safely used together, careful monitoring of the systemic blood pressure is recommended during coadministration, especially during the first one to three weeks of therapy.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468533, 'Fostamatinib', 'Nisoldipine', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', 'DDInter', 1767317476, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1468534, 'Gadopentetic acid', 'Nisoldipine', 'Moderate', 'Coadministration of calcium channel blockers may increase the cardiovascular effects of ionic X-ray contrast media. The mechanism is unknown.', 'DDInter', 1767317476, 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension. Use of a nonionic contrast agent, such as iopamidol, may be considered instead of ionic contrast where clinically appropriate.', 'Synergism', 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
