INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241566, 'Dicoumarol', 'Clofibrate', 'Major', 'Fibric acid derivatives may enhance the hypoprothrombinemic effect of coumarin-type oral anticoagulants.  The mechanism may involve displacement of anticoagulant from plasma protein binding sites.', NULL, 'Caution should be exercised when fibrates are prescribed to patients receiving oral anticoagulant therapy.', NULL, 'Caution should be exercised when fibrates are prescribed to patients receiving oral anticoagulant therapy.  Frequent prothrombin determinations are advisable until prothrombin level has stabilized.  Some experts recommend that the anticoagulant dose be reduced by approximately one-third to one-half initially, then gradually adjusted as necessary according to INR (International Normalized Ratio) monitoring.  Patients should be advised to notify their physician if they experience potential signs of excessive anticoagulation such as unusual or prolonged bleeding, bruising, vomiting, change in stool or urine color, headache, dizziness, or weakness.', 'Distribution', 'Mipomersen, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cerivastatin, Lomitapide, Niacin, Bempedoic acid, Alirocumab', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/268246/', '[1] "Product Information. Lopid (gemfibrozil)." Parke-Davis  (2002):[2] Harvengt C, Heller F, Desager JP "Hypolipidemic and hypouricemic action of fenofibrate in various types of hyperlipoproteinemias." Artery 7 (1980):  73-82[3] "Product Information. Tricor (fenofibrate)." Abbo