INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241760, 'Duvelisib', 'Propafenone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of propafenone, which may result in proarrhythmic events and exaggerated beta-adrenergic blocking activity.', NULL, 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors.  Therapeutic response to propafenone as well as clinical and electrocardiographic evidence of toxicity should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the propafenone dosage adjusted as necessary.  Patients should be monitored for potential adverse effects such as new or worsening arrhythmias or heart failure, edema, bradycardia, and atrioventricular block.  Simultaneous use of propafenone with both a CYP450 3A4 inhibitor and a CYP450 2D6 inhibitor (or in patients with CYP450 2D6 deficiency) should be avoided.', 'Metabolism', 'Dabrafenib, Acalabrutinib, Pexidartinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 'Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/269087/', '[1] Botsch S, Gautier JC, Beaune P, Eichelbaum M, Kroemer HK "Identification and characterization of the cytochrome P450 enzymes involved in N-dealkylation of propafenone: molecular base for interaction potential and variable disposition of active metabolites." Mol Pharmacol 43 (1993):  120-6[2] "Product Information. Rythmol SR (propa