INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (241761, 'Duvelisib', 'Selumetinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5.  Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, a reduction in the dosage of selumetinib is recommended.  Patients receiving selumetinib 25 mg/m2 twice daily should have the dosage reduced to 20 mg/m2 twice daily, and those receiving 20 mg/m2 twice daily should have the dosage reduced to 15 mg/m2 twice daily.  Further dosage adjustments should be made according to clinical response and tolerance.  Please refer to the product labeling for more detailed information on dosing adjustments.  After discontinuation of the strong or moderate CYP450 3A4 inhibitor for 3 elimination half-lives, the selumetinib dosage that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 'Futibatinib, Copanlisib, Alpelisib, Dacomitinib, Lenvatinib, Erdafitinib, Trametinib, Afatinib, Alectinib, Duvelisib, Tivozanib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/269107/', '[1] "Product Information. Koselugo (selumetinib)." A