    ) VALUES (1468657, 'Verteporfin', 'Nisoldipine', 'Minor', 'Drugs that enhance the uptake of verteporfin into the vascular epithelium may increase the therapeutic effect of this drug. If clinically appropriate, the morning dose of calcium channel blocker may be skipped on the day of the procedure to minimize the effect.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
