    ) VALUES (1468877, 'Triamterene', 'Nizatidine', 'Minor', 'Ranitidine, famotidine, and nizatidine may interfere with the absorption of triamterene. The diuretic effect of triamterene may be decreased when it is taken with any of these drugs. No special precaution appears necessary.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
