    ) VALUES (1468885, 'Entacapone', 'Norepinephrine', 'Moderate', 'COMT inhibitors affect the elimination of drugs metabolized by catechol-O-methyltransferase (COMT) and may potentiate their effects. Arrhythmias, increased heart rate, and blood pressure changes may occur. Entacapone has increased heart rates up to 80% when coadministered with isoproterenol and epinephrine. Ventricular tachycardia has also been reported.', NULL, 'The manufacturer recommends that caution be exercised when these drugs are given together.', NULL, 'The manufacturer recommends that caution be exercised when these drugs are given together. Monitoring for changes in hemodynamic status and ECG is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
