INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242094, 'Acrivastine', 'Meperidine', 'Moderate', 'The central nervous system and respiratory depressant effects of meperidine may be potentiated by concomitant use of other agents with CNS depressant effects.', NULL, 'Caution and dosage adjustments are advisable when meperidine is used in combination with other narcotic analgesics, general anesthetics, phenothiazines, sedative-hypnotics, tranquilizers, tricyclic antidepressants, or other CNS depressants such as alcohol.', NULL, 'Caution and dosage adjustments are advisable when meperidine is used in combination with other narcotic analgesics, general anesthetics, phenothiazines, sedative-hypnotics, tranquilizers, tricyclic antidepressants, or other CNS depressants such as alcohol.  A lower dosage of meperidine should be considered initially, then titrated carefully according to pain level and clinical response.  Meperidine dosage reductions of 25% to 50% have been recommended for patients receiving phenothiazines and other tranquilizers.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position, and to notify their physician if they experience dizziness, lightheadedness, orthostasis, syncope, tachycardia, or excessive CNS effects that interfere with their normal activities.  Patients should also avoid driving or operating hazardous machinery until they know how these medications affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/272256/', '[1] Lambertsen CJ, Wendel H, Longenhagen JB "The separate and combined respiratory effects of chlorpromazine and meperidine in normal men controlled at 46 mm