INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242239, 'Hydromorphone', 'Selegiline', 'Major', 'Coadministration of monoamine oxidase inhibitors (MAOIs), such as selegiline (an MAO-B inhibitor), with opioid analgesics may result in serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors.', NULL, 'Concomitant use of MAOIs, such as selegiline, with opioid analgesics should generally be avoided.', NULL, 'Concomitant use of MAOIs, such as selegiline, with opioid analgesics should generally be avoided.  At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with an opioid analgesic.  If coadministration is unavoidable, patients should be monitored for signs and symptoms of serotonin syndrome which may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.  Some authorities consider the use of selegiline with opioid analgesics to be contraindicated (UK).', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/273367/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Zelapar (selegiline)." Valeant Pharmaceuticals  (2006):[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242240, 'Hydromorphone', 'Tolevamer', 'Moderate', 'Coadministration with medications that can cause constipation such as opioids may increase the risk of intestinal injuries associated with the use of sodium polystyrene sulfonate.', NULL, 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.', NULL, 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.  The prescribing information recommends avoiding the use of sodium polystyrene sulfonate in patients who are at risk for developing constipation or impaction, including those with a history of impaction, chronic constipation, inflammatory bowel disease, ischemic colitis, vascular intestinal atherosclerosis, previous bowel resection, or bowel obstruction, as well as those who have not had a bowel movement post-surgery.  If clinically significant constipation develops, treatment with sodium polystyrene sulfonate should be discontinued until normal bowel motion is resumed.  Concomitant administration of sorbitol is not recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/273368/', '[1] Foresti V "Intestinal obstruction due to kayexalate in a patient concurrently treated with aluminum hydroxide and morphine sulfate." Clin Nephrol 41 (1994):  252[2] "Product Information. Kayexalate (sodium polystyrene sulfonate)." Sanofi Winthrop Pharmaceuticals  (2001):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767365210);
