INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242285, 'Dimethyl fumarate', 'Melphalan flufenamide', 'Moderate', 'The use of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with other immunosuppressive or myelosuppressive agents may increase the risk of infections.  Treatment with MMF or its prodrugs can cause lymphopenia as well as serious and potentially life-threatening opportunistic infections (e.g., progressive multifocal leukoencephalopathy (PML), herpes zoster, herpes simplex, West Nile virus, cytomegalovirus, Candida, Aspergillus, Nocardia, Listeria monocytogenes, Mycobacterium tuberculosis), and the risk may be theoretically potentiated in the presence of other agents that also suppress the immune system or bone marrow function.', NULL, 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.', NULL, 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.  Until further information is available, concomitant use should be avoided if possible.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/273442/', '[1] "Product Information. Vumerity (diroximel fumarate)." Alkermes, Inc  (2019):', 1767365210);
