INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242306, 'Echinacea', 'Diroximel fumarate', 'Minor', 'Theoretically, agents that are thought to have immunostimulant properties such as echinacea, vitamin E, cat''s claw, and zinc may antagonize the pharmacologic effects of immunosuppressants.  However, clinical cases of drug interactions have not been reported.', NULL, 'The effect of this interaction is not well established, and the possibility of an interaction should be considered if an adverse event occurs when combining Cat''s Claw with an immune-related drug.', NULL, 'The effect of this interaction is not well established, and the possibility of an interaction should be considered if an adverse event occurs when combining Cat''s Claw with an immune-related drug.', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/273594/', '[1] Miller LG "Herbal medicinals: selected clinical considerations focusing on known or potential drug-herb interactions." Arch Intern Med 158 (1998):  2200-11[2] Pepping J "Echinacea." Am J Health Syst Pharm 56 (1999):  121-2[3] Izzo AA, Ernst E "Interactions between herbal medicines and prescribed drugs: a systematic review." Drugs 61 (2001):  2163-75[4] Therapeutic Research Faculty "Natural Medicines Comprehensive Database.  http://www.naturaldatabase.com"   (2008):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242307, 'Diroximel fumarate', 'Melphalan flufenamide', 'Moderate', 'The use of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with other immunosuppressive or myelosuppressive agents may increase the risk of infections.  Treatment with MMF or its prodrugs can cause lymphopenia as well as serious and potentially life-threatening opportunistic infections (e.g., progressive multifocal leukoencephalopathy (PML), herpes zoster, herpes simplex, West Nile virus, cytomegalovirus, Candida, Aspergillus, Nocardia, Listeria monocytogenes, Mycobacterium tuberculosis), and the risk may be theoretically potentiated in the presence of other agents that also suppress the immune system or bone marrow function.', NULL, 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.', NULL, 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.  Until further information is available, concomitant use should be avoided if possible.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/273610/', '[1] "Product Information. Vumerity (diroximel fumarate)." Alkermes, Inc  (2019):', 1767365210);
