INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242315, 'Palbociclib', 'Methadone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of methadone, which is metabolized primarily by CYP450 3A4, 2B6, 2C19, and to a lesser extent by CYP450 2C9 and 2D6.', NULL, 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors.  Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of the CYP450 3A4 inhibitor in patients who are stabilized on their methadone regimen.  Patients should be advised to report excessive drowsiness, nausea, or asthenia to their physician, and to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.  ECG monitoring should be considered for patients on methadone with heart or liver disease; conduction abnormalities; electrolyte disturbances (i.e., hypokalemia, hypomagnesemia); concomitant use of drugs that may cause QT prolongation or electrolyte loss; concomitant use of CYP450 3A4 inhibitors; or use of methadone at dosages greater than 200 mg daily.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/273677/', '[1] Bell J, Seres V, Bowron P, Lewis J, Batey R "The use of serum methadone levels in patients receiving methadone maintenance." Clin Pharmacol Ther 43 (1988):  623-9[2] Iribarne C, Berthou F, Baird S, Dreano Y, Picart D, Bail JP, Beaune P, Menez JF "Involvement of cytochrome P450 3A4 enzyme in the N-demethylation of methadone in human liver microsomes." Chem Res Toxicol 9 (1996):  365-73[3] Oda Y, Kharasch ED "Metabolism of methadone and levo-alpha-acetylmethadol (LAAM) by human intestinal cytochrome P450 3A4 (CYP3A4): potential contribution of intestinal metabolism to presystemic clearance and bioactivation." J Pharmacol Exp Ther 298 (2001):  1021-32[4] Krantz MJ, Lewkowiez L, Hays H, et al. "Torsade de pointes associated with very-high-dose methadone." Ann Intern Med 137 (2002):  501-4[5] Foster DJ, Somogyi AA, Bochner F "Methadone N-demethylation in human liver microsomes: lack of stereoselectivity and involvement of CYP3A4." Br J Clin Pharmacol 47 (1999):  403-12[6] Ehret GB, Desmeules JA, Broers B "Methadone-associated long QT syndrome: improving pharmacotherapy for dependence on illegal opioids and lessons learned for pharmacology." Expert Opin Drug Saf 6 (2007):  289-303[7] Moody DE, Alburges ME, Parker RJ, Collings JM, Strong JM "The involvement of cytochrome P450 3A4 in the N-demethylation of L-alpha-acetylmethadol (LAAM), norLAAM, and methadone." Drug Metab Dispos 25 (1997):  1347-53[8] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences  (2012):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242316, 'Pralsetinib', 'Methadone', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/273680/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation  (2020):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242317, 'Tolevamer', 'Methadone', 'Moderate', 'Coadministration with medications that can cause constipation such as opioids may increase the risk of intestinal injuries associated with the use of sodium polystyrene sulfonate.', NULL, 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.', NULL, 'Because opioids commonly cause constipation, caution is advised when used during treatment with sodium polystyrene sulfonate.  The prescribing information recommends avoiding the use of sodium polystyrene sulfonate in patients who are at risk for developing constipation or impaction, including those with a history of impaction, chronic constipation, inflammatory bowel disease, ischemic colitis, vascular intestinal atherosclerosis, previous bowel resection, or bowel obstruction, as well as those who have not had a bowel movement post-surgery.  If clinically significant constipation develops, treatment with sodium polystyrene sulfonate should be discontinued until normal bowel motion is resumed.  Concomitant administration of sorbitol is not recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/273681/', '[1] Foresti V "Intestinal obstruction due to kayexalate in a patient concurrently treated with aluminum hydroxide and morphine sulfate." Clin Nephrol 41 (1994):  252[2] "Product Information. Kayexalate (sodium polystyrene sulfonate)." Sanofi Winthrop Pharmaceuticals  (2001):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242318, 'Isocarboxazid', 'Disopyramide', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination.  Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome.  Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation.  Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures.  Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.  Use of neuroleptics in combination with other neuroleptics or anticholinergic agents may increase the risk of tardive dyskinesia.  In addition, some neuroleptics and tricyclic antidepressants may cause prolongation of the QT interval and theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.  Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.  Ambulatory patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.  A reduction in anticholinergic dosages may be necessary if excessive adverse effects develop.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/273693/', '[1] Stadnyk AN, Glezos JD "Drug-induced heat stroke." Can Med Assoc J 128 (1983):  957-9[2] Zelman S, Guillan R "Heat stroke in phenothiazine-treated patients: a report of three fatalities." Am J Psychiatry 126 (1970):  1787-90[3] Mann SC, Boger WP "Psychotropic drugs, summer heat and humidity, and hyperplexia: a danger restated." Am J Psychiatry 135 (1978):  1097-100[4] Warnes H, Lehmann HE, Ban TA "Adynamic ileus during psychoactive medication: a report of three fatal and five severe cases." Can Med Assoc J 96 (1967):  1112-3[5] Gershon S, Neubauer H, Sundland DM "Interaction between some anticholinergic agents and phenothiazines." Clin Pharmacol Ther 6 (1965):  749-56[6] Sarnquist F, Larson CP Jr "Drug-induced heat stroke." Anesthesiology 39 (1973):  348-50[7] Johnson AL, Hollister LE, Berger PA "The anticholinergic intoxication syndrome: diagnosis and treatment." J Clin Psychiatry 42 (1981):  313-7[8] Lee BS "Possibility of hyperpyrexia with antipsychotic and anticholinergic drugs." J Clin Psychiatry 47 (1986):  571[9] Forester D "Fatal drug-induced heat stroke." JACEP 7 (1978):  243-4[10] Moreau A, Jones BD, Banno V "Chronic central anticholinergic toxicity in manic depressive illness mimicking dementia." Can J Psychiatry 31 (1986):  339-41[11] Hvizdos AJ, Bennett JA, Wells BG, Rappaport KB, Mendel SA "Anticholinergic psychosis in a patient receiving usual doses of haloperidol." Clin Pharm 2 (1983):  174-8[12] Cohen MA, Alfonso CA, Mosquera M "Development of urinary retention during treatment with clozapine and meclizine [published erratum appears in Am J Psychiatry 1994 Jun;151(6):952]." Am J Psychiatry 151 (1994):  619-20[13] "Product Information. Cogentin (benztropine)." Merck & Co., Inc  (2001):[14] Kulik AV, Wilbur R "Delirium and stereotypy from anticholinergic antiparkinson drugs." Prog Neuropsychopharmacol Biol Psychiatry 6 (1982):  75-82[15] "Product Information. Artane (trihexyphenidyl)." Lederle Laboratories  (2001):', 1767365210);
