        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1468942, 'Tranylcypromine', 'Norepinephrine', 'Moderate', 'While monoamine oxidase inhibitors (MAOIs) reportedly do not significantly enhance the hypertensive effects of direct-acting sympathomimetic amines, caution is nevertheless advised when these agents are used together. Some cases of increased heart rate and blood pressure have been reported.', NULL, 'Blood pressure and heart rate should be monitored during coadministration of direct-acting sympathomimetic agents with MAOIs or other agents that possess MAOI activity (e.', NULL, 'Blood pressure and heart rate should be monitored during coadministration of direct-acting sympathomimetic agents with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
