    ) VALUES (1469056, 'Norfloxacin', 'Melatonin', 'Minor', 'Concomitant administration of CYP450 1A1 or 1A2 inhibitors such as estrogens or quinolones may increase the serum concentration of melatonin. The clinical significance is unknown.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
