    ) VALUES (1469174, 'Zolmitriptan', 'Norfloxacin', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of zolmitriptan, which is a substrate of the isoenzyme.', NULL, 'It is recommended that not more than 5 mg of zolmitriptan be taken in a 24-hour period during concomitant use with CYP450 1A2 inhibitors.', NULL, 'It is recommended that not more than 5 mg of zolmitriptan be taken in a 24-hour period during concomitant use with CYP450 1A2 inhibitors. Patients should be advised to notify their doctor promptly if they experience increased sedation, dizziness, paraesthesia, chest pain or tightness, shortness of breath, and/or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
