INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242566, 'Fostamatinib', 'Entrectinib', 'Moderate', 'Coadministration of fostamatinib with inhibitors of CYP450 3A4 may increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration.  Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9.  In vitro, R406 is also a substrate of the P-glycoprotein (P-gp) efflux transporter.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Pralsetinib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Avapritinib, Selumetinib, Capmatinib, Ibrutinib, Duvelisib, Tivozanib, More', 'Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Phylloquinone, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, Epinephrine, Coagulation Factor IX Human', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/275913/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals  (2018):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242567, 'Ibrutinib', 'Entrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ibrutinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if ibrutinib is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if ibrutinib is prescribed in combination with CYP450 3A4 inhibitors.  Patients should be closely monitored for signs of ibrutinib toxicity such as myelosuppression, bleeding, infection, and renal impairment.  Dosage reduction in accordance with ibrutinib product labeling may be necessary if an interaction is suspected.', 'Metabolism', 'Dabrafenib, Dacomitinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Tivozanib, Brigatinib, Axitinib, Lenvatinib, More', 'Futibatinib, Entrectinib, Regorafenib, Copanlisib, Trametinib, Erlotinib, Ponatinib, Neratinib, Erdafitinib, Nintedanib, Capmatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/275915/', '[1] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc  (2013):[2] "Product Information. Qelbree (viloxazine)." Supernus Pharmaceuticals Inc  (2021):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242568, 'Entrectinib', 'Lurbinectedin', 'Moderate', 'Coadministration with a CYP450 3A4 inhibitor may increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity.', NULL, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with lurbinectedin.', NULL, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with lurbinectedin.  Patients should be advised to contact their physician if they experience symptoms suggestive of myelosuppression or hepatotoxicity, such as fever or any other signs of infection, unusual bruising or bleeding; or nausea or vomiting, or pain on the right side of the abdomen.', 'Metabolism', 'Pralsetinib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, More', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Porfimer sodium, Selinexor, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/275920/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals  (2020):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242569, 'Entrectinib', 'Macitentan', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 or moderate dual or combined inhibitors of CYP450 3A4 and CYP450 2C9 may increase the plasma concentrations of macitentan.  Macitentan is primarily metabolized by CYP450 3A4 and to a minor extent by CYP450 2C8, CYP450 2C9 and CYP450 2C19.', NULL, 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.', NULL, 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.  The product labeling recommends avoiding concomitant use with potent inhibitors (e.g., protease inhibitors, clarithromycin, cobicistat, conivaptan, delavirdine, itraconazole, ketoconazole, nefazodone, posaconazole, voriconazole).  The manufacturer of macitentan also recommends avoiding concomitant use with moderate dual inhibitors of CYP450 3A4 and 2C9 (e.g., fluconazole, amiodarone) or in combination with both a moderate CYP450 3A4 inhibitor and a moderate CYP450 2C9 inhibitor.', 'Metabolism', 'Dabrafenib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Metyrosine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/275921/', '[1] "Product Information. Opsumit (macitentan)." Actelion Pharmaceuticals US Inc  (2013):', 1767369485);
