INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242579, 'Enzalutamide', 'Tivozanib', 'Major', 'Coadministration of tivozanib with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of tivozanib, which has been shown to be a substrate of the isoenzyme.', NULL, 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer should generally be avoided.', NULL, 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer should generally be avoided.  If coadministration is necessary, some authorities advise caution, with clinical and laboratory monitoring to be considered whenever a strong CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, More', 'Alectinib, Dacomitinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/276023/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Fotivda (tivozanib)." Aveo Pharmaceuticals, Inc.  (2021):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242580, 'Epinephrine (ophthalmic)', 'Deserpidine', 'Moderate', 'Postganglionic adrenergic blocking agents may potentiate the pharmacologic effects of direct-acting sympathomimetic amines, while the hypotensive effect of the former may be diminished.  Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings, thus they may sensitize adrenergic receptors to direct-acting sympathomimetics.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.  Conversely, alternatives to postganglionic adrenergic blocking agents should be considered in patients treated with sympathomimetic amines.  If the combination is used, blood pressure and heart rate should be closely monitored.', 'Antagonism', 'Prazosin, Clonidine, Hydralazine, Guanfacine, Clonidine, Methyldopa', 'Tetracycline, Minocycline, Amphotericin B, Miconazole, Doxycycline, Metronidazole, Clotrimazole, Acetylsalicylic acid, Chlorhexidine, Neomycin, Formoterol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/276026/', '[1] Spiers AS, Calne DB "Action of dopamine on the human iris." Br Med J 4 (1969):  333-5[2] Muelheims GH, Entrup RW, Paiewonsky D, Mierzwiak DS "Increased sensitivity of the heart to catecholamine-induced arrhythmias following guanethidine." Clin Pharmacol Ther 6 (1965):  757-62[3] Limbird LE eds., Gilman AG, Hardman JG "Goodman and Gilman''s the Pharmacological Basis of Therapeutics." New York, NY: McGraw-Hill  (1995):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242581, 'Epinephrine (ophthalmic)', 'Ergometrine', 'Major', 'Additive or synergistic increases in blood pressure and/or ischemic response may occur when ergot alkaloids are combined with peripheral or central vasoconstrictors.  Ergot alkaloids produce arterial vasoconstriction by stimulating alpha-adrenergic and serotonin receptors and inhibiting endothelial-derived relaxation factor release.', NULL, 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.', NULL, 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.  If concomitant use is required, patients should be monitored for excessive vasoconstriction and have vital signs measured regularly.  Patients should also be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Amphotericin B, Hydrocortisone, Metronidazole, Prednisolone, Triamcinolone, Acetylsalicylic acid, Chlorhexidine, Neomycin, Formoterol, Cromoglicic acid, Ipratropium, More', 'Misoprostol, Dinoprostone, Oxytocin', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/276027/', '[1] Buchanan N, Cane RD, Miller M "Symmetrical gangrene of the extremities associated with the use of dopamine subsequent to ergometrine administration." Intensive Care Med 3 (1977):  55-6[2] Barthel W, Glusa E, Koth W "Interactions of dihydroergotamine with etilefrine in human leg veins in vitro and in situ." Int J Clin Pharmacol Ther Toxicol 25 (1987):  63-9[3] "Product Information. D.H.E. 45 (dihydroergotamine)." Sandoz Pharmaceuticals Corporation  (2002):[4] "Product Information. Bellergal-S (ergotamine)." Sandoz Pharmaceuticals Corporation  (2002):[5] "Product Information. ProAmatine (midodrine)." Roberts Pharmaceutical Corporation  (2001):[6] Chuang SS "Finger ischemia secondary to the synergistic agonist effect of norepinephrine and ergonovine and in a burn patient." Burns 29 (2003):  92-4', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242582, 'Guanadrel', 'Epinephrine (ophthalmic)', 'Moderate', 'Postganglionic adrenergic blocking agents may potentiate the pharmacologic effects of direct-acting sympathomimetic amines, while the hypotensive effect of the former may be diminished.  Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings, thus they may sensitize adrenergic receptors to direct-acting sympathomimetics.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.  Conversely, alternatives to postganglionic adrenergic blocking agents should be considered in patients treated with sympathomimetic amines.  If the combination is used, blood pressure and heart rate should be closely monitored.', 'Antagonism', 'Tetracycline, Minocycline, Amphotericin B, Miconazole, Doxycycline, Metronidazole, Clotrimazole, Acetylsalicylic acid, Chlorhexidine, Neomycin, Formoterol, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/276029/', '[1] Spiers AS, Calne DB "Action of dopamine on the human iris." Br Med J 4 (1969):  333-5[2] Muelheims GH, Entrup RW, Paiewonsky D, Mierzwiak DS "Increased sensitivity of the heart to catecholamine-induced arrhythmias following guanethidine." Clin Pharmacol Ther 6 (1965):  757-62[3] Limbird LE eds., Gilman AG, Hardman JG "Goodman and Gilman''s the Pharmacological Basis of Therapeutics." New York, NY: McGraw-Hill  (1995):', 1767