INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242932, 'Pegfilgrastim', 'Melphalan flufenamide', 'Moderate', 'The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established.  Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the prescribing information recommends that pegfilgrastim and eflapegrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the prescribing information recommends that pegfilgrastim and eflapegrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', 'Others', 'Interferon alfa-n1, Oprelvekin, Elapegademase, Interferon beta-1b, Peginterferon alfa-2a, Peginterferon beta-1a, Interferon alfacon-1, Interferon beta-1a, Histamine, Pegademase, Peginterferon alfa-2b, Glatiramer', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/278511/', '[1] "Product Information. Neulasta (pegfilgrastim)." Amgen  (2002):[2] "Product Information. Rolvedon (eflapegrastim)." Spectrum Pharmaceuticals Inc  (2022):', 1767369485);
