INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (242992, 'Gentamicin', 'Botulinum toxin type A', 'Major', 'The effects of botulinum toxin may be potentiated by neuromuscular blockers, aminoglycoside antibiotics, and other drugs that can interfere with neuromuscular transmission.  Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive neuromuscular blocking effects may occur when these agents are used together.', NULL, 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission.', NULL, 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission.  These drugs should be avoided in patients suspected of having botulinum toxin overdose or poisoning.  Because the effects of botulinum toxin products may spread from the area of injection, patients should be monitored closely for adverse effects such as asthenia, generalized muscle weakness, diplopia, blurred vision, ptosis, dysphagia, dysphonia, dysarthria, urinary incontinence and breathing difficulties, which can occur hours to weeks after injection.  Swallowing and breathing difficulties may be life-threatening or fatal.  Patients with peripheral motor neuropathic diseases, amyotrophic lateral sclerosis, or neuromuscular junctional disorders such as myasthenia gravis or Lambert-Eaton syndrome may be particularly susceptible.', 'Synergism', 'Gatifloxacin, Rifamycin, Lomefloxacin, Ampicillin, Azithromycin, Ofloxacin, Acyclovir, Ciprofloxacin, Famciclovir, Cefuroxime, Trifluridine, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/279102/', '[1] "Product Information. Myobloc (rimabotulinumtoxinB)." Elan Pharmaceuticals[2] "Product Information. Dysport (abobotulinumtoxinA)." Tercica Inc[3] "Product Information. Botox (onabotulinumtoxinA)." Allergan Inc[4] "Product Information. Xeomin (incobotulinumtoxinA)." Merz Pharmaceuticals  (2022):[5] "Product Information. Jeuveau (prabotulinumtoxinA)." Evolus, Inc.  (2022):[6] "Product Information. Daxxify (daxibotulinumtoxinA)." Revance Therapeutics, Inc.  (2022):', 1767369485);
