INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1469472, 'Xylometazoline (nasal)', 'Nortriptyline', 'Moderate', 'Coadministration with tri- or tetra-cyclic antidepressants may theoretically potentiate the sympathomimetic effects (e.g., hypertension, tachycardia) of systemically absorbed xylometazoline following topical (i.e., nasal) administration. The proposed mechanism may involve additive or synergistic increases in circulating catecholamines, such as noradrenaline (norepinephrine).', 'DDInter', 1767317476, 'Until further information is available, caution is advised if a tri- or tetra-cyclic antidepressant is used concomitantly with topical xylometazoline. Blood pressure and heart rate should be monitored at regular intervals during concomitant therapy. A dose reduction or discontinuation of concomitant therapy should be considered in patients who experience a sustained increase in blood pressure or heart rate.', 'Synergism', 'Until further information is available, caution is advised if a tri- or tetra-cyclic antidepressant is used concomitantly with topical xylometazoline.', NULL, NULL, 'Moderate', 'pharmacodynamic');
