INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243148, 'Gemcitabine', 'Varicella Zoster Vaccine (Recombinant)', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition.  Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents (e.g., greater than or equal to 2 mg/kg/day or 20 mg/day of prednisone or equivalent for 14 consecutive days or more), or long-term topical or inhaled corticosteroids.', NULL, 'In general, the U.', NULL, 'In general, the U.S. Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.  However, higher dosages, more frequent boosters, and/or serological testing may be required in some cases.  Local guidelines and prescribing information for individual vaccines should be consulted.  For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease.  For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.  For SARS-CoV-2 (COVID-19) vaccines, vaccination should preferably be completed at least two weeks before initiation or resumption of immunosuppressive therapies; however, decisions to delay or temporarily withhold immunosuppressive therapy to complete COVID-19 vaccination should consider the individual''s risks relative to their underlying condition.  Additional shots and/or boosters may be appropriate depending on age, prior COVID-19 vaccine formulation(s) received, and other factors in individuals with moderate to severe immune compromise due to medical conditions or immunosuppressive medications or treatments (e.g., solid organ transplant recipients on immunosuppressive therapy; patients on active treatment for solid tumor and hematologic malignancies).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/279393/', '[1] "Product Information. Fluzone (influenza virus vaccine, inactivated)." Connaught Laboratories Inc[2] "Product Information. Omnihib (haemophilus b conjugate vaccine (obsolete))." SmithKline Beecham[3] "Product Information. Havrix (hepatitis A adult vaccine)." SmithKline Beecham[4] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993):  1-18[5] "Product Information. Imovax Rabies (rabies vaccine, human diploid cell)." sanofi pasteur  (2022):[6] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc.  (2003):[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline  (2022):[9] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence.  https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf"   (2019):[10] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations.  https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chapter_6.pdf"   (2019):[11] CDC Centers for Disease Control and Prevention "Interim Clinical Considerations for Use of COVID-19 Vaccines Currently Approved or Authorized in the United States.  https://www.cdc.gov/vaccines/covid-19/clinical-considerations/interim-considerations-us.html"   (2022):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243149, 'Gemfibrozil', 'Macitentan', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 or moderate dual or combined inhibitors of CYP450 3A4 and CYP450 2C9 may increase the plasma concentrations of macitentan.  Macitentan is primarily metabolized by CYP450 3A4 and to a minor extent by CYP450 2C8, CYP450 2C9 and CYP450 2C19.', NULL, 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.', NULL, 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.  The product labeling recommends avoiding concomitant use with potent inhibitors (e.g., protease inhibitors, clarithromycin, cobicistat, conivaptan, delavirdine, itraconazole, ketoconazole, nefazodone, posaconazole, voriconazole).  The manufacturer of macitentan also recommends avoiding concomitant use with moderate dual inhibitors of CYP450 3A4 and 2C9 (e.g., fluconazole, amiodarone) or in combination with both a moderate CYP450 3A4 inhibitor and a moderate CYP450 2C9 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/279394/', '[1] "Product Information. Opsumit (macitentan)." Actelion Pharmaceuticals US Inc  (2013):', 1767363028);
